import React from 'react';
import logo from './logo.svg';
import './App.css';


class App extends React.Component {
    constructor(props){
      super(props);
      this.state = {
        month: '',
        day: '',
      }
    }

    handleSubmit(event){
      alert("You have selected a date");
      this.setState({month: event.target.month.value});
      this.setState({day: event.target.day.value});
    }

    render() {
      return (
        <form onSubmit={this.handleSubmit}>
          <label>
            Please enter a date to see forecasted weather in Singapore:<br></br>
            (Example: Nov 9)<br></br>
            <br></br>  
          </label>  
          <label>
            Month (where 1 represents Jan, and 12 represents Dec):<br></br>
            <input type="text" value={this.state.month}></input><br></br>
            <br></br>
          </label>
          <label>
            Day (1 to 31):<br></br>
            <input type="text" value={this.state.day}></input><br></br>
            <br></br>
          </label>
          <input type="submit" value="Submit" />
        </form>
      );
    }
}

