// src/reducers/index.js
import {ADD_ACTIVITY} from '../constants/action-types';

const initialState = {
    //activities: []
    activities: [{id:1,title:"Learning React",description:"how to learn react",date:"30/4/2019"}]
}

function rootReducer(state=initialState, action){
    if (action.type === ADD_ACTIVITY) {
        return Object.assign({},{
            activities: state.activities.concat([{id:2,title:"Learning Javascript",description:"how to learn react",date:"30/4/2019"}])
        });
    }
    return state;
}

export default rootReducer;
