import React from 'react';
import logo from './logo.svg';
import './App.css';


class App extends React.Component {
    constructor(props){
      super(props);
      this.state = {
        date:'',
        weather:'',
        temperature:'',
      }

      this.handleDateChange = this.handleDateChange.bind(this);
    }



    handleDateChange(event){
      this.setState({date: event.target.value});
      fetch("http://api.openweathermap.org/data/2.5/weather?q=Singapore&units=metric&APPID=d5fc7ebe276faf34c819f53c5660d7eb")
      .then(response => response.json())
      .then(data => this.setState({
        weather: data.weather[0].description,
        temperature: data.main.temp,
      }
      )
      );
    }

    handleSubmit(event){
      alert("You have selected a date");
    }

    // componentDidMount(){
    //   const city = "Singapore";
    //   fetch("http://api.openweathermap.org/data/2.5/weather?q=Singapore&units=metric&APPID=d5fc7ebe276faf34c819f53c5660d7eb")
    //   .then(response => response.json())
    //   .then(data => this.setState({
    //     weather: data.weather[0].description,
    //     temperature: data.main.temp,
    //   }
    //   )
    //   );
    // }

    render() {
      return (
        <form onSubmit={this.handleSubmit}>
          <label>
            Please enter a date to see forecasted weather in Singapore:<br></br>
            (Example: 28-04-2019)<br></br>
            <br></br>  
          </label>  

          <label>
            Date (DD-MM-YYYY) :<br></br>
            <input type="text" name="fieldDate" value={null} onChange={this.handleDateChange}></input><br></br>
            <br></br>
          </label>
          <input type="submit" value="Submit" /><br></br><br></br>


          <label>Current Weather: {this.state.weather}</label><br></br>
          <label>Current Temperature: {this.state.temperature}</label><br></br>


          <label>Forecasted Weather: {null}</label><br></br>
          <label>Forecasted Temperature: {null}</label><br></br>
        </form>

      );
    }
}

