import {connect} from 'react-redux';
import {deleteActivity} from '../../actions/index';

function mapDispatchToProps(dispatch){
    return {
        deleteActivity: activity => dispatch(deleteActivity(activity))
    };
}

class ConnectedActivity extends React.Component {
    constructor(props){
        super(props)
        this.state = {
            id:"",
            title:"",
            description:"",
            date:""
        }
        this.handleDelete = this.handleDelete.bind(this);
    }

    handleDelete(event){
        if(confirm("Are you sure you want to delete this activity?")){
            this.props.deleteActivity({
                id: this.state.id,
                title: this.state.title,
                description: this.state.description,
                date: this.state.date
            })
        }
    }

    render(){
        return(
        <div className="Activity">
            <label>Title: {activity.title}</label><br></br>
            <label>Description: {activity.description}</label><br></br>
            <label>Date: {activity.date}</label><br></br>
            <button onClick="this.handleDelete">Delete</button>
            <br></br>
        </div>
        );
    }
}

const Activity = connect(null,mapDispatchToProps)(ConnectedActivity);

export default Activity;
