import React from 'react';
import logo from './logo.svg';
import './App.css';


class App extends React.Component {
    constructor(props){
      super(props);
      this.state = {
        month: '',
        day: '',
        weather:'',
        temperature:'',
      }

      this.handleDayChange = this.handleDayChange.bind(this);
      this.handleMonthChange = this.handleMonthChange.bind(this);
    }



    handleMonthChange(event){
      this.setState({month: event.target.value});
    }

    handleDayChange(event){
      this.setState({day: event.target.value});
    }

    handleSubmit(event){
      alert("You have selected a date");
    }

    componentDidMount(){
      const city = "Singapore";
      fetch("http://api.openweathermap.org/data/2.5/weather?q=Singapore&APPID=d5fc7ebe276faf34c819f53c5660d7eb")
      .then(response => response.json())
      .then(data => this.setState({
        weather: data.weather[0].description,
        temperature: data.main.temp,
      }
      )
      );
    }

    render() {
      return (
        <form onSubmit={this.handleSubmit}>
          <label>
            Please enter a date to see forecasted weather in Singapore:<br></br>
            (Example: Nov 9)<br></br>
            <br></br>  
          </label>  
          <label>
            Month (where 1 represents Jan, and 12 represents Dec):<br></br>
            <input type="text" name="fieldMonth" value={null} onChange={this.handleMonthChange}></input><br></br>
            <br></br>
          </label>
          <label>
            Day (1 to 31):<br></br>
            <input type="text" name="fieldDay" value={null} onChange={this.handleDayChange}></input><br></br>
            <br></br>
          </label>
          <input type="submit" value="Submit" /><br></br>

          <label>Weather: {this.state.weather}</label><br></br>
          <label>Temperature: {this.state.temperature}</label><br></br>
        </form>

      );
    }
}

