import React, {Component} from 'react';
import {connect} from 'react-redux';
import {addActivity} from '../../actions/index';

function mapDispatchToProps(dispatch){
    return {
        addActivity: activity => dispatch(addActivity(activity))
    };
}

class ConnectedForm extends Component {
    constructor(){
        super();
        this.state = {
            id: 1,
            title: "",
            description: "",
            date: "",
        };
        this.handleChangeTitle = this.handleChangeTitle.bind(this);
        this.handleChangeDesc = this.handleChangeDesc.bind(this);
        this.handleChangeDate = this.handleChangeDate.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChangeTitle(event){
        this.setState({[event.target.id]: event.target.value});
    }

    handleChangeDesc(event){
        this.setState({[event.target.id]: event.target.value});
    }
    
    handleChangeDate(event){
        this.setState({[event.target.id]: event.target.value});
    }

    handleSubmit(event){
        event.preventDefault();

        const {id} = this.state;
        const {title} = this.state;
        const {description} = this.state;
        const {date} = this.state;
        this.props.addActivity({id,title,description,date})

        // reset form values to ""
        this.setState({id: this.state.id++});
        this.setState({title: ""});
        this.setState({description: ""});
        this.setState({date: ""});
    }

    render() {
        const {title} = this.state;
        const {description} = this.state;
        const {date} = this.state;
        return (
            <form onSubmit={this.handleSubmit}>
                <label>Title: </label><br></br>
                <input id="title" type="text" value={title} onChange={this.handleChangeTitle}></input><br></br>

                <label>Description: </label><br></br>
                <input id="description" type="text" value={description} onChange={this.handleChangeDesc}></input><br></br>

                <label>Date: </label><br></br>
                <input id="date" type="text" value={date} onChange={this.handleChangeDate}></input><br></br>

                <br></br>

                <button className="submit" type="submit">Add Activity</button>
            </form>
        )
    }
}

const Form = connect(null, mapDispatchToProps) (ConnectedForm);

export default Form;
