// src/reducers/index.js
import {ADD_ACTIVITY, DELETE_ACTIVITY} from '../constants/action-types';

const initialState = {
    //activities: []
    activities: [{id:1,title:"Learning React",description:"how to learn react",date:"30/4/2019"}]
}

function rootReducer(state=initialState, action){
    if (action.type === ADD_ACTIVITY) {
        return Object.assign({},{
            activities: state.activities.concat([action.payload])
        });
    } else if (action.type === DELETE_ACTIVITY) {
        let copy = Object.assign({},{
            activites: state.activities
        });
        return copy.filter(activity => activity.id !== action.payload.id)
        
        // for (const [index,value] of state.entries){
        //     if(value.id === action.payload.id){
        //     return state.slice(0,index).concat(state.slice(index+1));
        //     }
        // }
    } else {
        return state;
    }
}

export default rootReducer;
