import React, {Component} from 'react';
import {connect} from 'react-redux';
import {addActivity} from '../../actions/index';

class ConnectedForm extends Component {
    constructor(){
        super();
        this.state = {
            id: 1,
            title: "",
            description: "",
            date: "",
        }
        this.handleChangeTitle = this.handleChangeTitle.bind(this);
        this.handleChangeDesc = this.handleChangeDesc.bind(this);
        this.handleChangeDate = this.handleChangeDate.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChangeTitle(event){
        this.setState({title: event.target.value});
    }

    handleChangeDesc(event){
        this.setState({description: event.target.value});
    }
    
    handleChangeDate(event){
        this.setState({date: event.target.value});
    }

    handleSubmit(event){
        event.preventDefault();
        // this.props.addActivity({
        //     id: this.state.id,
        //     title: this.state.title,
        //     description: this.state.description,
        //     date: this.state.date,
        // })

        const [id,title,description,date] = this.state;
        this.props.addActivity({id,title,description,date})

        // reset form values to ""
        this.setState({id: this.state.id++});
        this.setState({title: ""});
        this.setState({description: ""});
        this.setState({date: ""});
    }

    render() {
        return (
            <form onSubmit={this.handleSubmit}>
                <label>Title: </label><br></br>
                <input className="title" type="text" value={null} onChange={this.handleChangeTitle}></input><br></br>

                <label>Description: </label><br></br>
                <input className="desc" type="text" value={null} onChange={this.handleChangeDesc}></input><br></br>

                <label>Date: </label><br></br>
                <input className="date" type="text" value={null} onChange={this.handleChangeDate}></input><br></br>

                <br></br>

                <button className="submit" type="submit">Add Activity</button>
            </form>
        )
    }
}

function mapDispatchToProps(dispatch){
    return {
        addActivity: activity => dispatch(addActivity(activity))
    };
}

const Form = connect(null, mapDispatchToProps)(ConnectedForm);

export default Form;
